import React from 'react'
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';

function Favourites() {

    const [favouriteRecipesId, setFavouriteRecipesId] = useState([]);
    const [favouriteRecipes,setFavouriteRecipes] = useState([]);

    useEffect(() => {
        getfavouriteRecipes();
       }, []);

    const displayFavouriteRecipes =  (favRecipes) => {
        favRecipes.forEach( async (recipe) => {
            let favouriteRecipe = await axios.get(`https://api.spoonacular.com/recipes/${recipe.recipe_id}/information?apiKey=${process.env.REACT_APP_API_KEY}`);
            
            setFavouriteRecipes(favouriteRecipe);
        });
    
    }

    const getfavouriteRecipes = async () => {
       
        let response = await axios.get("http://localhost:5000");
        let recipes = await response.data;
       
        setFavouriteRecipesId(recipes);
        // console.log(recipes);

        displayFavouriteRecipes(recipes);
    };
  
    return (
        <Grid>
          {favouriteRecipesId.map((item) => {
            return (
              <Card key={item.recipe_id}>
                <Link to={"/recipe/" + item.recipe_id}>
                  <img src={item.image} />
                  <h4>{item.title}</h4>
                </Link>
              </Card>
            )
          })}
    
        </Grid>
    
      )
    }
    
    const Grid = styled.div`
      display:grid;
      grid-template-columns:repeat(auto-fit,minmax(20rem,1fr));
      grid-gap:1rem;
    `
    
    const Card = styled.div`
    img{
        width:90%;
        border-radius:2rem;
    }
    
    h4{
        text-align:center;
        padding:1rem;
        color:black;
    
    }
    
    a{
      text-decoration:none;
    }
    `

export default Favourites
